:root {
  --primary-gradient: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  --secondary-gradient: linear-gradient(135deg, #f093fb 0%, #f5576c 100%);
  --success-gradient: linear-gradient(135deg, #4facfe 0%, #00f2fe 100%);
  --warning-gradient: linear-gradient(135deg, #43e97b 0%, #38f9d7 100%);
  --dark-color: #2d3748;
  --light-color: #f7fafc;
  --border-radius: 12px;
  --shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
  --transition: all 0.3s ease;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  background: var(--light-color);
  color: var(--dark-color);
  line-height: 1.6;
}

.app-container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 20px;
}

.app-header {
  text-align: center;
  margin-bottom: 40px;
  padding: 40px 20px;
  background: var(--primary-gradient);
  color: white;
  border-radius: var(--border-radius);
  box-shadow: var(--shadow);
  animation: fadeInUp 0.8s ease;
}

.app-header h1 {
  font-size: 2.5rem;
  margin-bottom: 10px;
  font-weight: 700;
}

.app-header p {
  font-size: 1.2rem;
  opacity: 0.9;
}

.dashboard {
  display: grid;
  gap: 30px;
}

.data-controls {
  background: white;
  padding: 30px;
  border-radius: var(--border-radius);
  box-shadow: var(--shadow);
  animation: fadeIn 0.6s ease 0.2s both;
}

.control-panel h2 {
  margin-bottom: 20px;
  color: var(--dark-color);
  font-size: 1.5rem;
}

.control-buttons {
  display: flex;
  gap: 15px;
  margin-bottom: 25px;
  flex-wrap: wrap;
}

.btn {
  padding: 12px 24px;
  border: none;
  border-radius: 8px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: var(--transition);
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
}

.btn-primary {
  background: var(--success-gradient);
  color: white;
}

.btn-secondary {
  background: var(--secondary-gradient);
  color: white;
}

.btn-danger {
  background: var(--warning-gradient);
  color: white;
  padding: 6px 12px;
  font-size: 0.9rem;
}

.stats-display {
  display: flex;
  gap: 30px;
  flex-wrap: wrap;
}

.stat-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 15px 25px;
  background: var(--light-color);
  border-radius: 8px;
  border: 2px solid #e2e8f0;
}

.stat-label {
  font-size: 0.9rem;
  color: #718096;
  margin-bottom: 5px;
}

.stat-value {
  font-size: 1.8rem;
  font-weight: 700;
  background: var(--primary-gradient);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.charts-section {
  background: white;
  padding: 30px;
  border-radius: var(--border-radius);
  box-shadow: var(--shadow);
  animation: fadeIn 0.6s ease 0.4s both;
}

.charts-section h2 {
  margin-bottom: 25px;
  font-size: 1.5rem;
  color: var(--dark-color);
}

.charts-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
  gap: 25px;
}

.chart-container {
  background: var(--light-color);
  padding: 20px;
  border-radius: var(--border-radius);
  border: 1px solid #e2e8f0;
  transition: var(--transition);
}

.chart-container:hover {
  transform: translateY(-5px);
  box-shadow: var(--shadow);
}

.chart-container h3 {
  margin-bottom: 15px;
  text-align: center;
  color: var(--dark-color);
  font-size: 1.1rem;
}

.chart-container canvas {
  max-width: 100%;
  height: auto;
}

.data-table-section {
  background: white;
  padding: 30px;
  border-radius: var(--border-radius);
  box-shadow: var(--shadow);
  animation: fadeIn 0.6s ease 0.6s both;
}

.data-table-section h2 {
  margin-bottom: 25px;
  font-size: 1.5rem;
  color: var(--dark-color);
}

.table-container {
  overflow-x: auto;
  border-radius: 8px;
  border: 1px solid #e2e8f0;
}

.data-table {
  width: 100%;
  border-collapse: collapse;
  background: white;
}

.data-table th,
.data-table td {
  padding: 12px 15px;
  text-align: left;
  border-bottom: 1px solid #e2e8f0;
}

.data-table th {
  background: var(--primary-gradient);
  color: white;
  font-weight: 600;
  position: sticky;
  top: 0;
}

.data-table tr:hover {
  background-color: #f7fafc;
}

.notification {
  position: fixed;
  top: 20px;
  right: 20px;
  background: var(--success-gradient);
  color: white;
  padding: 15px 20px;
  border-radius: 8px;
  box-shadow: var(--shadow);
  display: flex;
  align-items: center;
  gap: 10px;
  transform: translateX(400px);
  transition: transform 0.3s ease;
  z-index: 1000;
}

.notification.show {
  transform: translateX(0);
}

.notification-close {
  background: none;
  border: none;
  color: white;
  font-size: 1.2rem;
  cursor: pointer;
  padding: 0;
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@media (max-width: 768px) {
  .app-container {
    padding: 10px;
  }

  .app-header h1 {
    font-size: 2rem;
  }

  .charts-grid {
    grid-template-columns: 1fr;
  }

  .control-buttons {
    flex-direction: column;
  }

  .stats-display {
    flex-direction: column;
    gap: 15px;
  }

  .data-table {
    font-size: 0.9rem;
  }
}

