class DataAnalyticsApp {
  constructor() {
    this.data = [];
    this.charts = {};
    this.init();
  }

  init() {
    this.loadData();
    this.setupEventListeners();
    this.renderCharts();
    this.updateStats();
    this.renderDataTable();
  }

  loadData() {
    const storedData = localStorage.getItem('analyticsData');
    if (storedData) {
      this.data = JSON.parse(storedData);
    } else {
      this.generateInitialData();
    }
  }

  saveData() {
    localStorage.setItem('analyticsData', JSON.stringify(this.data));
  }

  generateInitialData() {
    const categories = ['Technology', 'Finance', 'Healthcare', 'Education', 'Retail', 'Manufacturing'];
    const sampleData = [
      { id: '1', name: 'Quarterly Revenue', category: 'Finance', value: 125000, timestamp: new Date('2024-01-15').getTime() },
      { id: '2', name: 'User Growth', category: 'Technology', value: 24500, timestamp: new Date('2024-01-20').getTime() },
      { id: '3', name: 'Patient Satisfaction', category: 'Healthcare', value: 94, timestamp: new Date('2024-01-25').getTime() },
      { id: '4', name: 'Student Enrollment', category: 'Education', value: 3200, timestamp: new Date('2024-02-01').getTime() },
      { id: '5', name: 'Sales Performance', category: 'Retail', value: 187500, timestamp: new Date('2024-02-05').getTime() },
      { id: '6', name: 'Production Output', category: 'Manufacturing', value: 45000, timestamp: new Date('2024-02-10').getTime() },
      { id: '7', name: 'Market Share', category: 'Finance', value: 18.5, timestamp: new Date('2024-02-15').getTime() },
      { id: '8', name: 'App Downloads', category: 'Technology', value: 1250000, timestamp: new Date('2024-02-20').getTime() },
      { id: '9', name: 'Treatment Success', category: 'Healthcare', value: 88, timestamp: new Date('2024-02-25').getTime() },
      { id: '10', name: 'Graduation Rate', category: 'Education', value: 92, timestamp: new Date('2024-03-01').getTime() }
    ];

    this.data = sampleData;
    this.saveData();
  }

  generateAdditionalData() {
    const categories = ['Technology', 'Finance', 'Healthcare', 'Education', 'Retail', 'Manufacturing'];
    const newData = [
      { id: Date.now().toString(), name: 'Cloud Infrastructure', category: 'Technology', value: 75000, timestamp: Date.now() },
      { id: (Date.now() + 1).toString(), name: 'Investment Returns', category: 'Finance', value: 12.8, timestamp: Date.now() },
      { id: (Date.now() + 2).toString(), name: 'Medical Research', category: 'Healthcare', value: 67, timestamp: Date.now() },
      { id: (Date.now() + 3).toString(), name: 'Online Courses', category: 'Education', value: 8900, timestamp: Date.now() },
      { id: (Date.now() + 4).toString(), name: 'E-commerce Sales', category: 'Retail', value: 234000, timestamp: Date.now() }
    ];

    this.data = [...this.data, ...newData];
    this.saveData();
    this.showNotification('5 new sample records generated successfully!');
    this.updateUI();
  }

  clearAllData() {
    if (confirm('Are you sure you want to clear all data? This action cannot be undone.')) {
      this.data = [];
      this.saveData();
      this.showNotification('All data has been cleared.');
      this.updateUI();
    }
  }

  deleteRecord(id) {
    this.data = this.data.filter(record => record.id !== id);
    this.saveData();
    this.showNotification('Record deleted successfully.');
    this.updateUI();
  }

  setupEventListeners() {
    document.getElementById('generateData').addEventListener('click', () => {
      this.generateAdditionalData();
    });

    document.getElementById('clearData').addEventListener('click', () => {
      this.clearAllData();
    });

    document.querySelector('.notification-close').addEventListener('click', () => {
      this.hideNotification();
    });
  }

  updateUI() {
    this.updateStats();
    this.renderCharts();
    this.renderDataTable();
  }

  updateStats() {
    document.getElementById('totalRecords').textContent = this.data.length;
    const categories = new Set(this.data.map(record => record.category));
    document.getElementById('totalCategories').textContent = categories.size;
  }

  renderDataTable() {
    const tbody = document.getElementById('dataTableBody');
    tbody.innerHTML = '';

    this.data.forEach(record => {
      const row = document.createElement('tr');
      row.innerHTML = `
        <td>${record.id}</td>
        <td>${record.name}</td>
        <td>${record.category}</td>
        <td>${record.value}</td>
        <td>${new Date(record.timestamp).toLocaleDateString()}</td>
        <td>
          <button class="btn btn-danger" onclick="app.deleteRecord('${record.id}')">
            Delete
          </button>
        </td>
      `;
      tbody.appendChild(row);
    });
  }

  renderCharts() {
    this.renderPieChart();
    this.renderLineChart();
    this.renderBarChart();
    this.renderDoughnutChart();
  }

  renderPieChart() {
    const ctx = document.getElementById('pieChart').getContext('2d');

    if (this.charts.pieChart) {
      this.charts.pieChart.destroy();
    }

    const categoryData = this.aggregateDataByCategory();

    this.charts.pieChart = new Chart(ctx, {
      type: 'pie',
      data: {
        labels: Object.keys(categoryData),
        datasets: [{
          data: Object.values(categoryData),
          backgroundColor: [
            '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40'
          ],
          borderWidth: 2,
          borderColor: '#fff'
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            position: 'bottom'
          }
        }
      }
    });
  }

  renderLineChart() {
    const ctx = document.getElementById('lineChart').getContext('2d');

    if (this.charts.lineChart) {
      this.charts.lineChart.destroy();
    }

    const timeData = this.aggregateDataByTime();

    this.charts.lineChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: Object.keys(timeData),
        datasets: [{
          label: 'Average Value',
          data: Object.values(timeData),
          borderColor: '#667eea',
          backgroundColor: 'rgba(102, 126, 234, 0.1)',
          borderWidth: 3,
          fill: true,
          tension: 0.4
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            display: false
          }
        },
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
  }

  renderBarChart() {
    const ctx = document.getElementById('barChart').getContext('2d');

    if (this.charts.barChart) {
      this.charts.barChart.destroy();
    }

    const categoryData = this.aggregateDataByCategory();

    this.charts.barChart = new Chart(ctx, {
      type: 'bar',
      data: {
        labels: Object.keys(categoryData),
        datasets: [{
          label: 'Total Value',
          data: Object.values(categoryData),
          backgroundColor: 'rgba(102, 126, 234, 0.8)',
          borderColor: '#667eea',
          borderWidth: 2
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            display: false
          }
        },
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
  }

  renderDoughnutChart() {
    const ctx = document.getElementById('doughnutChart').getContext('2d');

    if (this.charts.doughnutChart) {
      this.charts.doughnutChart.destroy();
    }

    const categoryData = this.aggregateDataByCategory();

    this.charts.doughnutChart = new Chart(ctx, {
      type: 'doughnut',
      data: {
        labels: Object.keys(categoryData),
        datasets: [{
          data: Object.values(categoryData),
          backgroundColor: [
            '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40'
          ],
          borderWidth: 2,
          borderColor: '#fff'
        }]
      },
      options: {
        responsive: true,
        plugins: {
          legend: {
            position: 'bottom'
          }
        }
      }
    });
  }

  aggregateDataByCategory() {
    const aggregated = {};
    this.data.forEach(record => {
      if (!aggregated[record.category]) {
        aggregated[record.category] = 0;
      }
      aggregated[record.category] += record.value;
    });
    return aggregated;
  }

  aggregateDataByTime() {
    const aggregated = {};
    this.data.forEach(record => {
      const month = new Date(record.timestamp).toLocaleDateString('en-US', { 
        month: 'short', 
        year: 'numeric' 
      });
      if (!aggregated[month]) {
        aggregated[month] = [];
      }
      aggregated[month].push(record.value);
    });

    const result = {};
    Object.keys(aggregated).forEach(month => {
      result[month] = aggregated[month].reduce((a, b) => a + b, 0) / aggregated[month].length;
    });

    return result;
  }

  showNotification(message) {
    const notification = document.getElementById('notification');
    const notificationText = document.querySelector('.notification-text');

    notificationText.textContent = message;
    notification.classList.add('show');

    setTimeout(() => {
      this.hideNotification();
    }, 4000);
  }

  hideNotification() {
    const notification = document.getElementById('notification');
    notification.classList.remove('show');
  }
}

let app;

document.addEventListener('DOMContentLoaded', () => {
  app = new DataAnalyticsApp();
});
