class TodoApp {
  constructor() {
    this.tasks = [];
    this.currentFilter = 'all';
    this.init();
  }

  init() {
    this.loadTasks();
    this.bindEvents();
    this.updateUI();
  }

  loadTasks() {
    const savedTasks = localStorage.getItem('todoAppTasks');
    if (savedTasks) {
      this.tasks = JSON.parse(savedTasks);
    } else {
      this.generateSampleData();
    }
  }

  saveTasks() {
    localStorage.setItem('todoAppTasks', JSON.stringify(this.tasks));
  }

  generateSampleData() {
    const sampleTasks = [
      { id: this.generateId(), text: 'Complete project proposal', completed: false, createdAt: new Date().toISOString() },
      { id: this.generateId(), text: 'Buy groceries for the week', completed: false, createdAt: new Date().toISOString() },
      { id: this.generateId(), text: 'Schedule team meeting', completed: true, createdAt: new Date().toISOString() },
      { id: this.generateId(), text: 'Read new book chapter', completed: false, createdAt: new Date().toISOString() },
      { id: this.generateId(), text: 'Prepare presentation slides', completed: false, createdAt: new Date().toISOString() },
      { id: this.generateId(), text: 'Call insurance company', completed: true, createdAt: new Date().toISOString() }
    ];
    this.tasks = sampleTasks;
    this.saveTasks();
    this.showNotification('Sample tasks generated!');
  }

  generateId() {
    return Date.now().toString() + Math.random().toString(36).substr(2, 9);
  }

  bindEvents() {
    const taskForm = document.getElementById('taskForm');
    const taskInput = document.getElementById('taskInput');
    const filterButtons = document.querySelectorAll('.filter-btn');
    const clearCompletedBtn = document.getElementById('clearCompleted');
    const generateSampleBtn = document.getElementById('generateSample');

    taskForm.addEventListener('submit', (e) => {
      e.preventDefault();
      this.addTask(taskInput.value.trim());
      taskInput.value = '';
      taskInput.focus();
    });

    filterButtons.forEach(btn => {
      btn.addEventListener('click', (e) => {
        filterButtons.forEach(b => b.classList.remove('active'));
        e.target.classList.add('active');
        this.currentFilter = e.target.dataset.filter;
        this.updateUI();
      });
    });

    clearCompletedBtn.addEventListener('click', () => {
      this.clearCompletedTasks();
    });

    generateSampleBtn.addEventListener('click', () => {
      this.generateSampleData();
      this.updateUI();
    });

    document.addEventListener('click', (e) => {
      if (e.target.classList.contains('task-checkbox')) {
        const taskId = e.target.closest('.task-item').dataset.id;
        this.toggleTask(taskId);
      }

      if (e.target.classList.contains('task-delete')) {
        const taskId = e.target.closest('.task-item').dataset.id;
        this.deleteTask(taskId);
      }
    });
  }

  addTask(text) {
    if (!text) {
      this.showNotification('Please enter a task!', 'error');
      return;
    }

    const newTask = {
      id: this.generateId(),
      text: text,
      completed: false,
      createdAt: new Date().toISOString()
    };

    this.tasks.unshift(newTask);
    this.saveTasks();
    this.updateUI();
    this.showNotification('Task added successfully!');
  }

  toggleTask(taskId) {
    const task = this.tasks.find(t => t.id === taskId);
    if (task) {
      task.completed = !task.completed;
      this.saveTasks();
      this.updateUI();
      this.showNotification(`Task marked as ${task.completed ? 'completed' : 'active'}`);
    }
  }

  deleteTask(taskId) {
    this.tasks = this.tasks.filter(t => t.id !== taskId);
    this.saveTasks();
    this.updateUI();
    this.showNotification('Task deleted!');
  }

  clearCompletedTasks() {
    const completedCount = this.tasks.filter(t => t.completed).length;
    if (completedCount === 0) {
      this.showNotification('No completed tasks to clear!', 'warning');
      return;
    }

    this.tasks = this.tasks.filter(t => !t.completed);
    this.saveTasks();
    this.updateUI();
    this.showNotification(`Cleared ${completedCount} completed tasks!`);
  }

  getFilteredTasks() {
    switch (this.currentFilter) {
      case 'active':
        return this.tasks.filter(t => !t.completed);
      case 'completed':
        return this.tasks.filter(t => t.completed);
      default:
        return this.tasks;
    }
  }

  updateUI() {
    this.renderTaskList();
    this.updateStats();
  }

  renderTaskList() {
    const taskList = document.getElementById('taskList');
    const filteredTasks = this.getFilteredTasks();

    if (filteredTasks.length === 0) {
      taskList.innerHTML = `
        <div class="empty-state">
          <p>No tasks found</p>
          <small>Add a new task to get started!</small>
        </div>
      `;
      return;
    }

    taskList.innerHTML = filteredTasks.map(task => `
      <li class="task-item ${task.completed ? 'completed' : ''}" data-id="${task.id}">
        <div class="task-checkbox ${task.completed ? 'checked' : ''}">
          ${task.completed ? 'âœ“' : ''}
        </div>
        <span class="task-text">${this.escapeHtml(task.text)}</span>
        <button class="task-delete" title="Delete task">ğŸ—‘ï¸</button>
      </li>
    `).join('');
  }

  updateStats() {
    const totalTasks = this.tasks.length;
    const activeTasks = this.tasks.filter(t => !t.completed).length;
    const completedTasks = this.tasks.filter(t => t.completed).length;

    document.getElementById('totalTasks').textContent = totalTasks;
    document.getElementById('activeTasks').textContent = activeTasks;
    document.getElementById('completedTasks').textContent = completedTasks;
  }

  showNotification(message, type = 'success') {
    const notification = document.getElementById('notification');
    notification.textContent = message;
    notification.className = 'notification show';

    if (type === 'error') {
      notification.style.background = 'var(--danger-color)';
    } else if (type === 'warning') {
      notification.style.background = 'var(--warning-color)';
    } else {
      notification.style.background = 'var(--success-color)';
    }

    setTimeout(() => {
      notification.classList.remove('show');
    }, 3000);
  }

  escapeHtml(text) {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
  }
}

// Initialize the app when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
  new TodoApp();
});
