class TaskManager {
  constructor() {
    this.dbName = 'TaskManagerDB';
    this.dbVersion = 1;
    this.storeName = 'tasks';
    this.currentFilter = 'all';
    this.init();
  }

  async init() {
    await this.initDB();
    await this.loadTasks();
    this.setupEventListeners();
    this.updateStats();
  }

  initDB() {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open(this.dbName, this.dbVersion);

      request.onerror = () => reject(request.error);
      request.onsuccess = () => {
        this.db = request.result;
        resolve();
      };

      request.onupgradeneeded = (event) => {
        const db = event.target.result;
        if (!db.objectStoreNames.contains(this.storeName)) {
          const store = db.createObjectStore(this.storeName, { keyPath: 'id', autoIncrement: true });
          store.createIndex('completed', 'completed', { unique: false });

          // Add sample tasks
          const sampleTasks = [
            { text: 'Welcome to your task manager! âœ¨', completed: false, createdAt: new Date() },
            { text: 'Click the checkbox to mark tasks complete', completed: false, createdAt: new Date() },
            { text: 'Use filters to view different task states', completed: true, createdAt: new Date() }
          ];

          sampleTasks.forEach(task => store.add(task));
        }
      };
    });
  }

  async loadTasks(filter = this.currentFilter) {
    const transaction = this.db.transaction([this.storeName], 'readonly');
    const store = transaction.objectStore(this.storeName);
    const request = store.getAll();

    request.onsuccess = () => {
      let tasks = request.result;

      if (filter === 'pending') {
        tasks = tasks.filter(task => !task.completed);
      } else if (filter === 'completed') {
        tasks = tasks.filter(task => task.completed);
      }

      this.renderTasks(tasks);
      this.updateStats();
    };
  }

  renderTasks(tasks) {
    const tasksList = document.getElementById('tasksList');

    if (tasks.length === 0) {
      tasksList.innerHTML = `
        <div class="empty-state">
          <div style="text-align: center; padding: 60px 20px; color: var(--text-secondary);">
            <div style="font-size: 4rem; margin-bottom: 16px;">ğŸ“</div>
            <h3 style="margin-bottom: 8px;">No tasks found</h3>
            <p>Add a new task to get started!</p>
          </div>
        </div>
      `;
      return;
    }

    tasksList.innerHTML = tasks.map(task => `
      <div class="task-item ${task.completed ? 'completed' : ''}" data-id="${task.id}">
        <div class="task-checkbox ${task.completed ? 'checked' : ''}" data-id="${task.id}">
          ${task.completed ? 'âœ“' : ''}
        </div>
        <div class="task-text">${task.text}</div>
        <button class="delete-btn" data-id="${task.id}">
          <span style="font-size: 1.2rem;">ğŸ—‘ï¸</span>
        </button>
      </div>
    `).join('');
  }

  async addTask(text) {
    if (!text.trim()) return;

    const task = {
      text: text.trim(),
      completed: false,
      createdAt: new Date()
    };

    const transaction = this.db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);
    const request = store.add(task);

    request.onsuccess = () => {
      this.loadTasks();
      document.getElementById('taskInput').value = '';
    };
  }

  async toggleTask(id) {
    const transaction = this.db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);
    const getRequest = store.get(Number(id));

    getRequest.onsuccess = () => {
      const task = getRequest.result;
      if (task) {
        task.completed = !task.completed;
        const updateRequest = store.put(task);
        updateRequest.onsuccess = () => this.loadTasks();
      }
    };
  }

  async deleteTask(id) {
    const transaction = this.db.transaction([this.storeName], 'readwrite');
    const store = transaction.objectStore(this.storeName);
    const request = store.delete(Number(id));

    request.onsuccess = () => {
      this.loadTasks();
    };
  }

  updateStats() {
    const transaction = this.db.transaction([this.storeName], 'readonly');
    const store = transaction.objectStore(this.storeName');
    const request = store.getAll();

    request.onsuccess = () => {
      const tasks = request.result;
      const total = tasks.length;
      const completed = tasks.filter(task => task.completed).length;
      const pending = total - completed;

      document.getElementById('totalTasks').textContent = total;
      document.getElementById('pendingTasks').textContent = pending;
      document.getElementById('completedTasks').textContent = completed;
    };
  }

  setupEventListeners() {
    // Add task
    document.getElementById('addTaskBtn').addEventListener('click', () => {
      const input = document.getElementById('taskInput');
      this.addTask(input.value);
    });

    document.getElementById('taskInput').addEventListener('keypress', (e) => {
      if (e.key === 'Enter') {
        this.addTask(e.target.value);
      }
    });

    // Filter buttons
    document.querySelectorAll('.filter-btn').forEach(btn => {
      btn.addEventListener('click', (e) => {
        document.querySelectorAll('.filter-btn').forEach(b => b.classList.remove('active'));
        e.target.classList.add('active');
        this.currentFilter = e.target.dataset.filter;
        this.loadTasks(this.currentFilter);
      });
    });

    // Event delegation for dynamic elements
    document.getElementById('tasksList').addEventListener('click', (e) => {
      const taskId = e.target.dataset.id;

      if (e.target.classList.contains('task-checkbox')) {
        this.toggleTask(taskId);
      } else if (e.target.classList.contains('delete-btn') || e.target.parentElement.classList.contains('delete-btn')) {
        const id = e.target.dataset.id || e.target.parentElement.dataset.id;
        this.deleteTask(id);
      }
    });
  }
}

// Initialize the app when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
  new TaskManager();
});
