class CRMDashboard {
  constructor() {
    this.customers = JSON.parse(localStorage.getItem('crm_customers')) || this.generateSampleData();
    this.currentView = 'dashboard';
    this.selectedCustomer = null;
    this.init();
  }

  generateSampleData() {
    const sampleData = [
      { id: 1, name: 'John Smith', email: 'john@example.com', phone: '+1-555-0101', status: 'active' },
      { id: 2, name: 'Sarah Johnson', email: 'sarah@example.com', phone: '+1-555-0102', status: 'active' },
      { id: 3, name: 'Mike Wilson', email: 'mike@example.com', phone: '+1-555-0103', status: 'inactive' },
      { id: 4, name: 'Emily Davis', email: 'emily@example.com', phone: '+1-555-0104', status: 'active' },
      { id: 5, name: 'David Brown', email: 'david@example.com', phone: '+1-555-0105', status: 'active' }
    ];
    localStorage.setItem('crm_customers', JSON.stringify(sampleData));
    return sampleData;
  }

  init() {
    this.setupEventListeners();
    this.renderDashboard();
  }

  setupEventListeners() {
    document.querySelectorAll('.nav-item').forEach(item => {
      item.addEventListener('click', (e) => {
        const view = e.target.dataset.view;
        this.switchView(view);
      });
    });

    document.getElementById('addCustomerBtn').addEventListener('click', () => {
      this.openCustomerModal();
    });

    document.getElementById('customerForm').addEventListener('submit', (e) => {
      e.preventDefault();
      this.saveCustomer();
    });

    document.getElementById('cancelBtn').addEventListener('click', () => {
      this.closeCustomerModal();
    });

    document.getElementById('searchInput').addEventListener('input', (e) => {
      this.filterCustomers(e.target.value);
    });

    document.getElementById('statusFilter').addEventListener('change', (e) => {
      this.filterCustomers(document.getElementById('searchInput').value, e.target.value);
    });
  }

  switchView(view) {
    document.querySelectorAll('.view').forEach(v => v.classList.remove('active'));
    document.querySelectorAll('.nav-item').forEach(item => item.classList.remove('active'));

    document.getElementById(view + 'View').classList.add('active');
    document.querySelector(`[data-view="${view}"]`).classList.add('active');

    this.currentView = view;

    if (view === 'customers') {
      this.renderCustomers();
    } else if (view === 'dashboard') {
      this.renderDashboard();
    }
  }

  renderDashboard() {
    const total = this.customers.length;
    const active = this.customers.filter(c => c.status === 'active').length;

    document.getElementById('totalCustomers').textContent = total;
    document.getElementById('activeCustomers').textContent = active;

    this.renderActivityTimeline();
  }

  renderActivityTimeline() {
    const timeline = document.getElementById('activityTimeline');
    const activities = [
      'New customer John Smith added',
      'Sarah Johnson updated contact information',
      'Monthly report generated',
      'Mike Wilson marked as inactive'
    ];

    timeline.innerHTML = activities.map(activity => 
      `<div class="activity-item">${activity}</div>`
    ).join('');
  }

  renderCustomers(filteredCustomers = null) {
    const customersToRender = filteredCustomers || this.customers;
    const grid = document.getElementById('customersGrid');

    grid.innerHTML = customersToRender.map(customer => `
      <div class="customer-card" data-id="${customer.id}">
        <div class="customer-name">${customer.name}</div>
        <div class="customer-email">${customer.email}</div>
        <div class="customer-phone">${customer.phone}</div>
        <span class="customer-status status-${customer.status}">${customer.status}</span>
      </div>
    `).join('');

    grid.querySelectorAll('.customer-card').forEach(card => {
      card.addEventListener('click', (e) => {
        const customerId = parseInt(e.currentTarget.dataset.id);
        this.showCustomerProfile(customerId);
      });
    });
  }

  filterCustomers(searchTerm = '', status = '') {
    let filtered = this.customers;

    if (searchTerm) {
      filtered = filtered.filter(customer => 
        customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        customer.email.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (status) {
      filtered = filtered.filter(customer => customer.status === status);
    }

    this.renderCustomers(filtered);
  }

  showCustomerProfile(customerId) {
    const customer = this.customers.find(c => c.id === customerId);
    if (!customer) return;

    this.selectedCustomer = customer;

    document.getElementById('customerProfile').innerHTML = `
      <h3>${customer.name}</h3>
      <p><strong>Email:</strong> ${customer.email}</p>
      <p><strong>Phone:</strong> ${customer.phone}</p>
      <p><strong>Status:</strong> <span class="status-${customer.status}">${customer.status}</span></p>
    `;

    this.switchView('profile');
  }

  openCustomerModal(customer = null) {
    const modal = document.getElementById('customerModal');
    const title = document.getElementById('modalTitle');
    const form = document.getElementById('customerForm');

    if (customer) {
      title.textContent = 'Edit Customer';
      document.getElementById('customerName').value = customer.name;
      document.getElementById('customerEmail').value = customer.email;
      document.getElementById('customerPhone').value = customer.phone;
      document.getElementById('customerStatus').value = customer.status;
    } else {
      title.textContent = 'Add Customer';
      form.reset();
    }

    modal.style.display = 'flex';
  }

  closeCustomerModal() {
    document.getElementById('customerModal').style.display = 'none';
  }

  saveCustomer() {
    const name = document.getElementById('customerName').value;
    const email = document.getElementById('customerEmail').value;
    const phone = document.getElementById('customerPhone').value;
    const status = document.getElementById('customerStatus').value;

    const newCustomer = {
      id: this.customers.length > 0 ? Math.max(...this.customers.map(c => c.id)) + 1 : 1,
      name,
      email,
      phone,
      status
    };

    this.customers.push(newCustomer);
    localStorage.setItem('crm_customers', JSON.stringify(this.customers));

    this.closeCustomerModal();
    this.renderCustomers();
    this.renderDashboard();
  }
}

document.addEventListener('DOMContentLoaded', () => {
  new CRMDashboard();
});
