class TaskManager {
  constructor() {
    this.dbName = 'TaskManagerDB';
    this.dbVersion = 1;
    this.storeName = 'tasks';
    this.currentFilter = 'all';
    this.init();
  }

  async init() {
    await this.initDB();
    await this.loadTasks();
    this.setupEventListeners();
    this.updateStats();
  }

  initDB() {
    return new Promise((resolve, reject) => {
      const request = indexedDB.open(this.dbName, this.dbVersion);

      request.onerror = () => reject(request.error);
      request.onsuccess = () => {
        this.db = request.result;
        resolve();
      };

      request.onupgradeneeded = (event) => {
        const db = event.target.result;
        if (!db.objectStoreNames.contains(this.storeName)) {
          const store = db.createObjectStore(this.storeName, { 
            keyPath: 'id', 
            autoIncrement: true 
          });
          store.createIndex('completed', 'completed', { unique: false });
          store.createIndex('createdAt', 'createdAt', { unique: false });
        }
      };
    });
  }

  async addTask(text) {
    if (!text.trim()) return;

    const task = {
      text: text.trim(),
      completed: false,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };

    return new Promise((resolve, reject) => {
      const transaction = this.db.transaction([this.storeName], 'readwrite');
      const store = transaction.objectStore(this.storeName);
      const request = store.add(task);

      request.onsuccess = () => {
        task.id = request.result;
        this.renderTask(task);
        this.updateStats();
        resolve(task);
      };
      request.onerror = () => reject(request.error);
    });
  }

  async loadTasks() {
    return new Promise((resolve, reject) => {
      const transaction = this.db.transaction([this.storeName], 'readonly');
      const store = transaction.objectStore(this.storeName);
      const request = store.getAll();

      request.onsuccess = () => {
        const tasks = request.result;
        if (tasks.length === 0) {
          this.preloadSampleTasks();
        } else {
          this.renderTasks(tasks);
        }
        resolve(tasks);
      };
      request.onerror = () => reject(request.error);
    });
  }

  async preloadSampleTasks() {
    const sampleTasks = [
      'Complete project proposal',
      'Schedule team meeting',
      'Review design mockups',
      'Update documentation',
      'Prepare presentation slides'
    ];

    for (const text of sampleTasks) {
      await this.addTask(text);
    }
  }

  async updateTask(id, updates) {
    return new Promise((resolve, reject) => {
      const transaction = this.db.transaction([this.storeName], 'readwrite');
      const store = transaction.objectStore(this.storeName);
      const getRequest = store.get(id);

      getRequest.onsuccess = () => {
        const task = getRequest.result;
        if (task) {
          Object.assign(task, updates, { updatedAt: new Date().toISOString() });
          const putRequest = store.put(task);
          putRequest.onsuccess = () => {
            this.updateTaskInDOM(id, task);
            this.updateStats();
            resolve(task);
          };
          putRequest.onerror = () => reject(putRequest.error);
        }
      };
      getRequest.onerror = () => reject(getRequest.error);
    });
  }

  async deleteTask(id) {
    return new Promise((resolve, reject) => {
      const transaction = this.db.transaction([this.storeName], 'readwrite');
      const store = transaction.objectStore(this.storeName);
      const request = store.delete(id);

      request.onsuccess = () => {
        this.removeTaskFromDOM(id);
        this.updateStats();
        resolve();
      };
      request.onerror = () => reject(request.error);
    });
  }

  renderTasks(tasks) {
    const tasksList = document.getElementById('tasksList');
    tasksList.innerHTML = '';

    const filteredTasks = this.filterTasks(tasks);
    filteredTasks.forEach(task => this.renderTask(task));

    this.toggleEmptyState();
  }

  renderTask(task) {
    const tasksList = document.getElementById('tasksList');
    const taskElement = document.createElement('li');
    taskElement.className = `task-item ${task.completed ? 'completed' : ''}`;
    taskElement.dataset.id = task.id;

    taskElement.innerHTML = `
      <div class="task-checkbox ${task.completed ? 'checked' : ''}" data-id="${task.id}">
        ${task.completed ? 'âœ“' : ''}
      </div>
      <div class="task-text">${this.escapeHtml(task.text)}</div>
      <div class="task-actions">
        <button class="delete-btn" data-id="${task.id}" title="Delete task">ğŸ—‘ï¸</button>
      </div>
    `;

    tasksList.appendChild(taskElement);
    this.toggleEmptyState();
  }

  updateTaskInDOM(id, task) {
    const taskElement = document.querySelector(`.task-item[data-id="${id}"]`);
    if (taskElement) {
      taskElement.className = `task-item ${task.completed ? 'completed' : ''}`;
      const checkbox = taskElement.querySelector('.task-checkbox');
      const text = taskElement.querySelector('.task-text');

      checkbox.className = `task-checkbox ${task.completed ? 'checked' : ''}`;
      checkbox.innerHTML = task.completed ? 'âœ“' : '';
      text.innerHTML = this.escapeHtml(task.text);

      if (this.currentFilter !== 'all' && !this.shouldShowTask(task)) {
        taskElement.style.display = 'none';
      } else {
        taskElement.style.display = 'flex';
      }
    }
  }

  removeTaskFromDOM(id) {
    const taskElement = document.querySelector(`.task-item[data-id="${id}"]`);
    if (taskElement) {
      taskElement.style.animation = 'slideIn 0.3s ease-out reverse';
      setTimeout(() => {
        taskElement.remove();
        this.toggleEmptyState();
      }, 300);
    }
  }

  filterTasks(tasks) {
    return tasks.filter(task => this.shouldShowTask(task));
  }

  shouldShowTask(task) {
    switch (this.currentFilter) {
      case 'completed':
        return task.completed;
      case 'pending':
        return !task.completed;
      default:
        return true;
    }
  }

  toggleEmptyState() {
    const emptyState = document.getElementById('emptyState');
    const tasksList = document.getElementById('tasksList');
    const visibleTasks = tasksList.querySelectorAll('.task-item[style*="display: flex"], .task-item:not([style])');

    if (visibleTasks.length === 0) {
      emptyState.classList.add('show');
    } else {
      emptyState.classList.remove('show');
    }
  }

  updateStats() {
    const transaction = this.db.transaction([this.storeName], 'readonly');
    const store = transaction.objectStore(this.storeName);
    const request = store.getAll();

    request.onsuccess = () => {
      const tasks = request.result;
      const total = tasks.length;
      const completed = tasks.filter(task => task.completed).length;
      const pending = total - completed;

      document.getElementById('totalTasks').textContent = total;
      document.getElementById('pendingTasks').textContent = pending;
      document.getElementById('completedTasks').textContent = completed;
    };
  }

  setupEventListeners() {
    const taskInput = document.getElementById('taskInput');
    const addTaskBtn = document.getElementById('addTaskBtn');
    const tasksList = document.getElementById('tasksList');
    const filterBtns = document.querySelectorAll('.filter-btn');

    addTaskBtn.addEventListener('click', () => {
      this.addTask(taskInput.value);
      taskInput.value = '';
      taskInput.focus();
    });

    taskInput.addEventListener('keypress', (e) => {
      if (e.key === 'Enter') {
        this.addTask(taskInput.value);
        taskInput.value = '';
      }
    });

    tasksList.addEventListener('click', (e) => {
      const target = e.target;

      if (target.classList.contains('task-checkbox')) {
        const id = parseInt(target.dataset.id);
        const taskElement = target.closest('.task-item');
        const completed = !taskElement.classList.contains('completed');
        this.updateTask(id, { completed });
      }

      if (target.classList.contains('delete-btn')) {
        const id = parseInt(target.dataset.id);
        this.deleteTask(id);
      }
    });

    filterBtns.forEach(btn => {
      btn.addEventListener('click', () => {
        filterBtns.forEach(b => b.classList.remove('active'));
        btn.classList.add('active');
        this.currentFilter = btn.dataset.filter;
        this.loadTasks();
      });
    });
  }

  escapeHtml(text) {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
  }
}

document.addEventListener('DOMContentLoaded', () => {
  new TaskManager();
});
