class TodoApp {
  constructor() {
    this.tasks = [];
    this.currentFilter = 'all';
    this.init();
  }

  init() {
    this.loadTasks();
    this.bindEvents();
    this.updateUI();
  }

  loadTasks() {
    const savedTasks = localStorage.getItem('todoAppTasks');
    if (savedTasks) {
      this.tasks = JSON.parse(savedTasks);
    } else {
      this.generateSampleData();
    }
  }

  saveTasks() {
    localStorage.setItem('todoAppTasks', JSON.stringify(this.tasks));
  }

  generateSampleData() {
    const sampleTasks = [
      { id: this.generateId(), text: 'Complete project proposal', completed: false, createdAt: Date.now() - 86400000 },
      { id: this.generateId(), text: 'Buy groceries for the week', completed: true, createdAt: Date.now() - 172800000 },
      { id: this.generateId(), text: 'Schedule team meeting', completed: false, createdAt: Date.now() - 43200000 },
      { id: this.generateId(), text: 'Read new book chapter', completed: false, createdAt: Date.now() - 21600000 },
      { id: this.generateId(), text: 'Prepare presentation slides', completed: true, createdAt: Date.now() - 345600000 },
      { id: this.generateId(), text: 'Call insurance company', completed: false, createdAt: Date.now() - 7200000 }
    ];
    this.tasks = sampleTasks;
    this.saveTasks();
    this.showNotification('Sample data generated!', 'success');
  }

  generateId() {
    return Date.now().toString() + Math.random().toString(36).substr(2, 9);
  }

  bindEvents() {
    const addTaskBtn = document.getElementById('addTaskBtn');
    const taskInput = document.getElementById('taskInput');
    const filterBtns = document.querySelectorAll('.filter-btn');
    const clearCompletedBtn = document.getElementById('clearCompletedBtn');
    const generateSampleBtn = document.getElementById('generateSampleBtn');
    const taskList = document.getElementById('taskList');

    addTaskBtn.addEventListener('click', () => this.addTask());

    taskInput.addEventListener('keypress', (e) => {
      if (e.key === 'Enter') {
        this.addTask();
      }
    });

    filterBtns.forEach(btn => {
      btn.addEventListener('click', (e) => {
        this.setFilter(e.target.dataset.filter);
      });
    });

    clearCompletedBtn.addEventListener('click', () => this.clearCompleted());
    generateSampleBtn.addEventListener('click', () => this.generateSampleData());

    taskList.addEventListener('click', (e) => {
      if (e.target.classList.contains('task-checkbox')) {
        this.toggleTask(e.target.closest('.task-item').dataset.id);
      } else if (e.target.classList.contains('delete-btn')) {
        this.deleteTask(e.target.closest('.task-item').dataset.id);
      }
    });
  }

  addTask() {
    const taskInput = document.getElementById('taskInput');
    const text = taskInput.value.trim();

    if (!text) {
      this.showNotification('Please enter a task!', 'error');
      return;
    }

    const newTask = {
      id: this.generateId(),
      text: text,
      completed: false,
      createdAt: Date.now()
    };

    this.tasks.unshift(newTask);
    this.saveTasks();
    this.updateUI();

    taskInput.value = '';
    taskInput.focus();
    this.showNotification('Task added successfully!', 'success');
  }

  toggleTask(taskId) {
    const task = this.tasks.find(t => t.id === taskId);
    if (task) {
      task.completed = !task.completed;
      this.saveTasks();
      this.updateUI();
      this.showNotification(`Task marked as ${task.completed ? 'completed' : 'active'}!`, 'success');
    }
  }

  deleteTask(taskId) {
    this.tasks = this.tasks.filter(t => t.id !== taskId);
    this.saveTasks();
    this.updateUI();
    this.showNotification('Task deleted!', 'success');
  }

  clearCompleted() {
    const completedCount = this.tasks.filter(t => t.completed).length;
    this.tasks = this.tasks.filter(t => !t.completed);
    this.saveTasks();
    this.updateUI();
    this.showNotification(`Cleared ${completedCount} completed tasks!`, 'success');
  }

  setFilter(filter) {
    this.currentFilter = filter;

    document.querySelectorAll('.filter-btn').forEach(btn => {
      btn.classList.remove('active');
    });

    document.querySelector(`[data-filter="${filter}"]`).classList.add('active');
    this.updateUI();
  }

  getFilteredTasks() {
    switch (this.currentFilter) {
      case 'active':
        return this.tasks.filter(t => !t.completed);
      case 'completed':
        return this.tasks.filter(t => t.completed);
      default:
        return this.tasks;
    }
  }

  updateUI() {
    this.renderTaskList();
    this.updateStats();
    this.toggleEmptyState();
  }

  renderTaskList() {
    const taskList = document.getElementById('taskList');
    const filteredTasks = this.getFilteredTasks();

    taskList.innerHTML = filteredTasks.map(task => `
      <div class="task-item ${task.completed ? 'completed' : ''}" data-id="${task.id}">
        <div class="task-checkbox ${task.completed ? 'checked' : ''}">
          ${task.completed ? 'âœ“' : ''}
        </div>
        <div class="task-text">${this.escapeHtml(task.text)}</div>
        <div class="task-actions">
          <button class="delete-btn" title="Delete task">ğŸ—‘ï¸</button>
        </div>
      </div>
    `).join('');
  }

  updateStats() {
    const totalTasks = this.tasks.length;
    const activeTasks = this.tasks.filter(t => !t.completed).length;
    const completedTasks = this.tasks.filter(t => t.completed).length;

    document.getElementById('totalTasks').textContent = totalTasks;
    document.getElementById('activeTasks').textContent = activeTasks;
    document.getElementById('completedTasks').textContent = completedTasks;
  }

  toggleEmptyState() {
    const emptyState = document.getElementById('emptyState');
    const taskList = document.getElementById('taskList');
    const hasTasks = this.tasks.length > 0;
    const hasFilteredTasks = this.getFilteredTasks().length > 0;

    if (!hasTasks) {
      emptyState.style.display = 'block';
      taskList.style.display = 'none';
    } else if (!hasFilteredTasks) {
      emptyState.innerHTML = `
        <div class="empty-icon">ğŸ”</div>
        <h3>No ${this.currentFilter} tasks</h3>
        <p>Try changing the filter or add new tasks!</p>
      `;
      emptyState.style.display = 'block';
      taskList.style.display = 'none';
    } else {
      emptyState.style.display = 'none';
      taskList.style.display = 'block';
    }
  }

  showNotification(message, type) {
    const notification = document.getElementById('notification');
    notification.textContent = message;
    notification.className = `notification ${type} show`;

    setTimeout(() => {
      notification.classList.remove('show');
    }, 3000);
  }

  escapeHtml(text) {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
  }
}

document.addEventListener('DOMContentLoaded', () => {
  new TodoApp();
});
